import React, { useState } from 'react';
import { ShoppingCart, Star, Clock, MapPin, Phone, X, Plus, Minus, Search } from 'lucide-react';
import './FoodyZone.css';

const FoodyZone = () => {
  const [cart, setCart] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [showCart, setShowCart] = useState(false);

  const menuItems = [
    {
      id: 1,
      title: "Bolded Eggs",
      description: "Fresh eggs with special seasoning, perfectly boiled to perfection.",
      price: 12.99,
      category: "breakfast",
      rating: 4.5,
      prepTime: "15 min",
      popular: true
    },
    {
      id: 2,
      title: "RAMEN",
      description: "Authentic Japanese ramen with rich broth and fresh noodles.",
      price: 14.99,
      category: "main",
      rating: 4.8,
      prepTime: "20 min",
      popular: true
    },
    {
      id: 3,
      title: "GRILLED CHICKEN",
      description: "Juicy chicken grilled with special herbs and spices.",
      price: 16.99,
      category: "main",
      rating: 4.7,
      prepTime: "25 min",
      popular: false
    },
    {
      id: 4,
      title: "SIS50",
      description: "Special meal combo with 50% discount!",
      price: "SIS50",
      originalPrice: 29.99,
      category: "deal",
      discount: "50% OFF",
      popular: true
    },
    {
      id: 5,
      title: "CHOCO CAKE",
      description: "Decadent chocolate cake with rich ganache topping.",
      price: 8.99,
      category: "dessert",
      rating: 4.9,
      prepTime: "10 min",
      popular: true
    },
    {
      id: 6,
      title: "MEGA BURGER",
      description: "Double patty burger with cheese and special sauce.",
      price: 13.99,
      category: "main",
      rating: 4.6,
      prepTime: "18 min",
      popular: false
    }
  ];

  const categories = [
    { id: 'all', name: 'All Items' },
    { id: 'main', name: 'Main Course' },
    { id: 'breakfast', name: 'Breakfast' },
    { id: 'dessert', name: 'Desserts' },
    { id: 'deal', name: 'Special Deals' }
  ];

  const addToCart = (item) => {
    const existingItem = cart.find(cartItem => cartItem.id === item.id);
    if (existingItem) {
      setCart(cart.map(cartItem =>
        cartItem.id === item.id
          ? { ...cartItem, quantity: cartItem.quantity + 1 }
          : cartItem
      ));
    } else {
      const finalPrice = item.price === "SIS50" ? item.originalPrice / 2 : item.price;
      setCart([...cart, { ...item, quantity: 1, finalPrice }]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter(item => item.id !== id));
  };

  const updateQuantity = (id, change) => {
    setCart(cart.map(item => {
      if (item.id === id) {
        const newQuantity = item.quantity + change;
        if (newQuantity < 1) {
          removeFromCart(id);
          return item;
        }
        return { ...item, quantity: newQuantity };
      }
      return item;
    }));
  };

  const getTotal = () => {
    return cart.reduce((total, item) => total + (item.finalPrice * item.quantity), 0).toFixed(2);
  };

  const getItemCount = () => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  const filteredItems = selectedCategory === 'all' 
    ? menuItems 
    : menuItems.filter(item => item.category === selectedCategory);

  return (
    <div className="foody-zone">
      {/* Header */}
      <header className="header">
        <div className="project-tag">Project 4 - Foody Zone</div>
        <h1 className="title">
          <span className="title-primary">Foody</span>
          <span className="title-secondary">Zone</span>
        </h1>
        
        <div className="search-bar">
          <Search className="search-icon" />
          <input type="text" placeholder="Search food..." />
        </div>

        <div className="cart-icon" onClick={() => setShowCart(true)}>
          <ShoppingCart />
          {cart.length > 0 && <span className="cart-badge">{getItemCount()}</span>}
        </div>
      </header>

      {/* Categories */}
      <div className="categories">
        {categories.map(cat => (
          <button
            key={cat.id}
            className={`category-btn ${selectedCategory === cat.id ? 'active' : ''}`}
            onClick={() => setSelectedCategory(cat.id)}
          >
            {cat.name}
          </button>
        ))}
      </div>

      {/* Features */}
      <div className="features">
        <div className="feature">
          <Clock /> <span>30 min delivery</span>
        </div>
        <div className="feature">
          <Star /> <span>4.8 Rating</span>
        </div>
        <div className="feature">
          <MapPin /> <span>Free delivery</span>
        </div>
        <div className="feature">
          <Phone /> <span>24/7 Support</span>
        </div>
      </div>

      {/* Menu Items */}
      <div className="menu-grid">
        {filteredItems.map(item => (
          <div key={item.id} className={`menu-card ${item.popular ? 'popular' : ''}`}>
            {item.popular && <div className="popular-badge">ðŸ”¥ Popular</div>}
            
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            
            <div className="item-details">
              <div className="rating">
                <Star /> <span>{item.rating}</span>
              </div>
              <div className="time">
                <Clock /> <span>{item.prepTime}</span>
              </div>
            </div>

            <div className="price-section">
              {item.category === 'deal' ? (
                <>
                  <span className="original-price">${item.originalPrice}</span>
                  <span className="deal-price">{item.price}</span>
                </>
              ) : (
                <span className="price">${item.price}</span>
              )}
              <button className="add-btn" onClick={() => addToCart(item)}>
                Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Cart Sidebar */}
      {showCart && (
        <div className="cart-overlay" onClick={() => setShowCart(false)}>
          <div className="cart-sidebar" onClick={(e) => e.stopPropagation()}>
            <div className="cart-header">
              <h3>Your Cart ({getItemCount()} items)</h3>
              <button className="close-btn" onClick={() => setShowCart(false)}>
                <X />
              </button>
            </div>

            <div className="cart-items">
              {cart.length === 0 ? (
                <p className="empty-cart">Your cart is empty</p>
              ) : (
                cart.map(item => (
                  <div key={item.id} className="cart-item">
                    <div className="cart-item-info">
                      <h4>{item.title}</h4>
                      <p>${item.finalPrice?.toFixed(2)}</p>
                    </div>
                    
                    <div className="quantity-controls">
                      <button onClick={() => updateQuantity(item.id, -1)}>
                        <Minus size={16} />
                      </button>
                      <span>{item.quantity}</span>
                      <button onClick={() => updateQuantity(item.id, 1)}>
                        <Plus size={16} />
                      </button>
                    </div>
                    
                    <button 
                      className="remove-btn"
                      onClick={() => removeFromCart(item.id)}
                    >
                      <X size={16} />
                    </button>
                  </div>
                ))
              )}
            </div>

            {cart.length > 0 && (
              <div className="cart-footer">
                <div className="total">
                  <span>Total:</span>
                  <span>${getTotal()}</span>
                </div>
                <button className="checkout-btn">Checkout</button>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Footer */}
      <footer className="footer">
        <p>Â© 2024 Foody Zone. All rights reserved.</p>
        <p>Delicious food delivered to your door!</p>
      </footer>
    </div>
  );
};

export default FoodyZone;
